    new_node->data = new_data; 

    new_node->next = NULL; 

    if (*head_ref == NULL) { 
        *head_ref = new_node; 
        return; 
    } 

    while (last->next != NULL) 
        last = last->next; 

    last->next = new_node; 
    return; 
} 

